import React,{Component} from 'react';
import './App.css'
import Axios from 'axios';
import DisplayWeather from './Component/DisplayWeather'
import Navbar from './Component/Navbar'

class App extends Component{

  state = {
    coords:{
      latitude:0,
      longitude:0
    },
    data:{},
    inputData:""
  }

  componentDidMount(){
    if(navigator.geolocation){
      navigator.geolocation.getCurrentPosition((position)=>{
       let newCoords = {
          latitude : position.coords.latitude,
          longitude : position.coords.longitude
        }
        this.setState({
          coords : newCoords
        });
        Axios.get(`http://api.weatherstack.com/current?access_key=0713ff5b232c2944d6864479e41b757d&query=
            ${this.state.coords.latitude},
            ${this.state.coords.longitude}`).then(res=>{
              let weatherData = {
                location:res.data.location.name,
                region: res.data.location.region,
                country: res.data.location.country,
                wind_speed: res.data.current.wind_speed,
                pressure: res.data.current.pressure,
                precip: res.data.current.precip,
                humidity: res.data.current.humidity,
                img: res.data.current.weather_icons,
                temperature: res.data.current.temperature,
                description: res.data.current.weather_descriptions[0]
              }
              this.setState({
                data:weatherData
              });
            })
      })
    }
    else{
      console.log("Not supported");
    }
  }

  render(){
      return (
        <div className="App">
          <Navbar cha/>
            <DisplayWeather weather={this.state.data}/>
        </div>
      );
  }
}


export default App
